import './TeamWeeklySummaries.css';
import moment from 'moment';
import parse from 'html-react-parser';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { toast } from 'react-toastify';
import { faCopy } from '@fortawesome/free-solid-svg-icons';

function TeamWeeklySummaries({ name, i, data, darkMode }) {
  const getWeekDates = weekIndex => ({
    fromDate: moment()
      .tz('America/Los_Angeles')
      .startOf('week')
      .subtract(weekIndex, 'week')
      .format('DD-MMM-YY'),
    toDate: moment()
      .tz('America/Los_Angeles')
      .endOf('week')
      .subtract(weekIndex, 'week')
      .format('DD-MMM-YY'),
  });
  console.log('darkMode', darkMode);
  return (
    <div
      className={`team-weekly-summaries ${darkMode ? 'bg-space-cadet' : ''}`}
      data-testid="team-weekly-summaries"
    >
      <div className="team-weekly-header">
        <h6 className="team-weekly-header-date">
          {getWeekDates(i).fromDate} to {getWeekDates(i).toDate}
        </h6>{' '}
        <h6>
          {i === 0 && !data.summary && `${name} did not submit a summary yet for this week.`}
          {data.summary && `Viewing ${name}'s summary`}
          {i !== 0 && !data.summary && `${name} did not submit a summary for this week.`}
        </h6>
      </div>
      {data.summary && (
        <div className="team-weekly-summary-container">
          <div className="team-week-summary-text">
            {' '}
            {darkMode
              ? parse('<span class=darkSpan >' + data.summary + '</span>')
              : parse(data.summary)}
          </div>

          <FontAwesomeIcon
            icon={faCopy}
            className="copy-icon"
            data-testid="copy-icon"
            onClick={() => {
              const parsedSummary = data.summary.replace(/<\/?[^>]+>|&nbsp;/g, '');
              navigator.clipboard.writeText(parsedSummary);
              toast.success('Summary Copied!');
            }}
          />
        </div>
      )}
    </div>
  );
}

export default TeamWeeklySummaries;

import React, { useState, useEffect } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Alert, Input } from 'reactstrap';
import AddProjectsAutoComplete from './AddProjectsAutoComplete';
import { boxStyle, boxStyleDark } from '~/styles';
import '../../Header/DarkMode.css';
import { toast } from 'react-toastify';
import axios from 'axios';
import { ENDPOINTS } from '~/utils/URL';

const AddProjectPopup = React.memo(props => {
  const { darkMode } = props;

  const closePopup = () => {
    props.onClose();
    setIsOpenDropdown(false);
    isSetShowAlert(false);
  };

  const [selectedProject, onSelectProject] = useState(undefined);
  const [isValidProject, onValidation] = useState(true);
  const [isShowAlert, isSetShowAlert] = useState(false);
  const [itemsDropdown, setItemsDropdown] = useState([]);
  const [isOpenDropdown, setIsOpenDropdown] = useState(false);
  const [isUserIsNotSelectedAutoComplete, isSetUserIsNotSelectedAutoComplete] = useState(false);
  const [dropdownText, setDropdownText] = useState('Unspecified');
  const [searchText, onInputChange] = useState('');
  const [allProjects, setAllProjects] = useState(props.projects);

  const formatText = result =>
    result
      .toLowerCase()
      .trim()
      .replace(/\s+/g, '');

  useEffect(() => {
    const categoriesProjects = props.projects.map(item => item.category);
    const uniqueCategoriesProjects = Array.from(new Set(categoriesProjects));
    setItemsDropdown(uniqueCategoriesProjects);
  }, [props.projects]);

  const onAssignProject = async () => {
    try {
      if (isUserIsNotSelectedAutoComplete) {
        const validateProjectName = validationProjectName();

        if (!validateProjectName) {
          isSetShowAlert(true);
          setIsOpenDropdown(true);
          return;
        }
      }

      if (selectedProject && !props.userProjectsById.some(x => x._id === selectedProject._id)) {
        await props.onSelectAssignProject(selectedProject);
        onSelectProject(undefined);
        if (props.handleSubmit !== undefined) {
          await props.handleSubmit();
        }
        toast.success('Project assigned successfully');
      } else {
        onValidation(false);
      }
    } catch (error) {}
  };

  const selectProject = project => {
    onSelectProject(project);
    onValidation(true);
    isSetUserIsNotSelectedAutoComplete(false);
  };

  useEffect(() => {
    onValidation(true);
  }, [props.open]);

  const finishFetch = status => {
    setIsOpenDropdown(false);

    toast.success(
      status === 200
        ? 'Project created successfully'
        : 'Project created successfully, but it is not possible to retrieve the new project.',
    );

    setDropdownText(dropdownText);
  };

  const format = result => result.toLowerCase();

  const validationProjectName = () =>
    props.projects.find(project => format(project.projectName) === format(searchText));

  const onCreateNewProject = async () => {
    if (isShowAlert) isSetShowAlert(false);

    // prettier-ignore
    if (searchText === '') {onValidation(false); onSelectProject(undefined); return;}

    const validateProjectName = validationProjectName();
    // prettier-ignore
    if (validateProjectName) { toast.error('This project already exists.'); return;}

    const urlCreateProject = ENDPOINTS.PROJECTS;

    const newProject = {
      projectName: searchText,
      projectCategory: dropdownText,
      isActive: true,
    };

    try {
      await axios.post(urlCreateProject, newProject);
      const url = ENDPOINTS.PROJECTS;
      const res = await axios.get(url);
      const status = res.status;
      const projects = res.data;
      if (status === 200) {
        const findNewProject = projects.filter((item, i) => i === 0)[0];
        selectProject(findNewProject);
        setAllProjects(res.data);
        finishFetch(status);
      } else {
        onInputChange('');
        finishFetch(status);
      }
    } catch (err) {
      toast.error('Project creation failed');
      onInputChange('');
    }
  };

  return (
    <Modal
      isOpen={props.open}
      toggle={closePopup}
      autoFocus={false}
      className={darkMode ? 'text-light dark-mode' : ''}
    >
      <ModalHeader className={darkMode ? 'bg-space-cadet' : ''} toggle={closePopup}>
        {isOpenDropdown ? 'Create' : '  Add'} Project{' '}
      </ModalHeader>
      <ModalBody className={darkMode ? 'bg-yinmn-blue' : ''} style={{ textAlign: 'center' }}>
        <>
          <div className="input-group-prepend" style={{ marginBottom: '10px' }}>
            <AddProjectsAutoComplete
              projectsData={allProjects}
              onDropDownSelect={selectProject}
              selectedProject={selectedProject}
              setIsOpenDropdown={setIsOpenDropdown}
              searchText={searchText}
              onInputChange={onInputChange}
              isSetUserIsNotSelectedAutoComplete={isSetUserIsNotSelectedAutoComplete}
              formatText={formatText}
            />
            <Button
              color={isOpenDropdown ? 'success' : 'primary'}
              style={darkMode ? {} : { ...boxStyle, marginLeft: '5px' }}
              onClick={isOpenDropdown ? onCreateNewProject : onAssignProject}
            >
              {isOpenDropdown ? 'Create' : 'Confirm'}
            </Button>
          </div>

          {isOpenDropdown && (
            <div
              className="input-group-prepend"
              style={{ marginBottom: '10px', display: 'flex', flexDirection: 'row', gap: '10px' }}
            >
              <Input
                type="select"
                onChange={e => setDropdownText(e.target.value)}
                value={dropdownText}
              >
                {itemsDropdown.map(item => {
                  return <option key={item}>{item}</option>;
                })}
              </Input>

              <Button
                color="danger"
                onClick={() => {
                  setIsOpenDropdown(false), isSetShowAlert(false), onInputChange('');
                }}
                style={{ width: '100%' }}
              >
                Cancel project creation
              </Button>
            </div>
          )}
        </>
        <div>
          {!isValidProject && selectedProject && (
            <Alert color="danger">Great idea, but they already have that one! Pick another! </Alert>
          )}
          {!isValidProject && !selectedProject && (
            <Alert color="danger">
              Hey, You need to {isOpenDropdown ? 'write the name of' : 'pick'} a project first!
            </Alert>
          )}

          {isShowAlert && <Alert color="danger">This project does not exist.</Alert>}
        </div>
      </ModalBody>
      <ModalFooter className={darkMode ? 'bg-yinmn-blue' : ''}>
        <Button color="secondary" onClick={closePopup} style={darkMode ? boxStyleDark : boxStyle}>
          Close
        </Button>
      </ModalFooter>
    </Modal>
  );
});
export default AddProjectPopup;

import React, { useEffect, useState } from 'react';
import { Dropdown, Input } from 'reactstrap';
import './TeamsAndProjects.css';
import { useSelector } from 'react-redux';

const AddProjectsAutoComplete = React.memo(props => {
  const [isOpen, toggle] = useState(false);
  const darkMode = useSelector(state => state.theme.darkMode);

  useEffect(() => {
    if (!props.selectedProject) props.onInputChange('');
    else props.onInputChange(props.selectedProject.projectName);
  }, [props.selectedProject]);

  return (
    <Dropdown
      isOpen={isOpen}
      toggle={() => {
        toggle(!isOpen);
      }}
      style={{ width: '100%', marginRight: '5px' }}
    >
      <Input
        type="text"
        value={props.searchText}
        autoFocus={true}
        onChange={e => {
          props.onInputChange(e.target.value);
          toggle(true);
          props.isSetUserIsNotSelectedAutoComplete(true);
        }}
        className={`${darkMode ? 'bg-darkmode-liblack border-0 text-light' : ''}`}
      />

      {props.searchText !== '' && props.projectsData && props.projectsData.length > 0 ? (
        <div
          tabIndex="-1"
          role="menu"
          aria-hidden="false"
          className={`dropdown-menu${isOpen ? ' show' : ''} ${
            darkMode ? 'bg-darkmode-liblack text-light' : ''
          }`}
          style={{ marginTop: '0px', width: '100%' }}
        >
          {props.projectsData
            .filter(project => {
              if (
                //prettier-ignore
                props.formatText(project.projectName).indexOf(props.formatText(props.searchText)) >-1
              ) {
                return project;
              }
            })
            .slice(0, 10)
            .map(item => (
              <div
                className="project-auto-complete"
                key={item._id}
                onClick={() => {
                  props.onInputChange(item.projectName);
                  toggle(false);
                  props.onDropDownSelect(item);
                }}
              >
                {item.projectName}
              </div>
            ))}

          {props.projectsData.every(
            item => props.formatText(item.projectName) !== props.formatText(props.searchText),
          ) && (
            <div
              className="project-auto-complete"
              onClick={() => {
                toggle(false);
                props.setIsOpenDropdown(true);
              }}
            >
              Create new project: {props.searchText}
            </div>
          )}
        </div>
      ) : (
        <></>
      )}
    </Dropdown>
  );
});

export default AddProjectsAutoComplete;

import React, { useEffect, useState } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Alert, Spinner } from 'reactstrap';
import AddTeamsAutoComplete from './AddTeamsAutoComplete';
import { useDispatch } from 'react-redux';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import '../../Header/DarkMode.css';
import { postNewTeam, getAllUserTeams } from '../../../../src/actions/allTeamsAction';
import axios from 'axios';
const AddTeamPopup = React.memo(props => {
  const { darkMode } = props;

  const dispatch = useDispatch();

  const [selectedTeam, onSelectTeam] = useState(undefined);
  const [isValidTeam, onValidation] = useState(true);
  const [isValidNewTeam, onNewTeamValidation] = useState(true);
  const [searchText, setSearchText] = useState(''); // add searchText state

  // states and onrs for the new team form
  const [isDuplicateTeam, setDuplicateTeam] = useState(false);
  const [isNotDisplayAlert, setIsNotDisplayAlert] = useState(true);
  const [isLoading, setIsLoading] = useState(false);

  const closePopup = () => {
    props.onClose();
    !isNotDisplayAlert && setIsNotDisplayAlert(true);
  };

  // prettier-ignore
  const format = result => result.toLowerCase().trim().replace(/\s+/g, '');

  const FilteredToTeamSpecific = arrayOrObj => {
    const result = props.teamsData.allTeams.filter(
      item => format(item.teamName) === format(searchText),
    );
    if (result.length > 0) {
      if (arrayOrObj) return result[0];
      else return result;
    } else return undefined;
  };

  const IfTheUserNotSelectedSuggestionAutoComplete = () => {
    // prettier-ignore
    if(searchText === '')  {onValidation(false);  return;}

    const filterTeamData = FilteredToTeamSpecific(true);

    if (filterTeamData) {
      onAssignTeam(filterTeamData);
      onValidation(true);
      !isNotDisplayAlert && setIsNotDisplayAlert(true);
    } else setIsNotDisplayAlert(false);
  };

  const onAssignTeam = result => {
    //  prettier-ignore
    if (!searchText) {onValidation(false);  return;} /* when the user typed nothing  */

    const idToCheck = result ? result._id : selectedTeam._id;

    const some = !props.userTeamsById.some(x => x._id === idToCheck);

    if ((result || selectedTeam) && some) {
      props.onSelectAssignTeam(result ? result : selectedTeam);

      setSearchText('');

      selectedTeam && (onSelectTeam(undefined), onValidation(false));
      closePopup(); // automatically closes the popup after team assigned
    } else
      toast.error(
        'Your user has been found in this team. Please select another team to add your user.',
      );
  };

  const axiosResponseExceededTimeout = source => {
    setIsLoading(false);
    source.cancel();
  };

  const onCreateTeam = async () => {
    if (searchText !== '') {
      const CancelToken = axios.CancelToken;
      const source = CancelToken.source();
      const timeout = setTimeout(() => axiosResponseExceededTimeout(source), 20000);

      setIsLoading(true);
      const response = await dispatch(postNewTeam(searchText, true, source));
      clearTimeout(timeout);
      if (response.status === 200) {
        setDuplicateTeam(false);
        !isNotDisplayAlert && setIsNotDisplayAlert(true);
        // Get updated teams list and select the new team
        await dispatch(getAllUserTeams());
        toast.success('Team created successfully');
        const newTeam = response.data; // Assuming response contains the new team data
        const updatedTeams = [...props.teamsData.allTeams, newTeam];
        props.teamsData.allTeams = updatedTeams;
        setIsLoading(false);
        onAssignTeam(newTeam);
      } else {
        setIsLoading(false);
        const messageToastError =
          response.status === 500
            ? 'No response received from the server'
            : 'Error occurred while creating team';
        response.status === 403 ? setDuplicateTeam(true) : toast.error(messageToastError);
      }
    } else {
      onNewTeamValidation(false);
    }
  };

  useEffect(() => {
    onValidation(true);
    onNewTeamValidation(true);
  }, [props.open]);

  return (
    <Modal
      isOpen={props.open}
      toggle={closePopup}
      autoFocus={false}
      className={darkMode ? 'text-light dark-mode' : ''}
    >
      <ModalHeader className={darkMode ? 'bg-space-cadet' : ''} toggle={closePopup}>
        Add Team
      </ModalHeader>
      <ModalBody className={darkMode ? 'bg-yinmn-blue' : ''} style={{ textAlign: 'center' }}>
        <label className={darkMode ? 'text-light' : ''} style={{ textAlign: 'left' }}>
          Add to Team
        </label>
        <div className="input-group-prepend" style={{ marginBottom: '10px' }}>
          <AddTeamsAutoComplete
            teamsData={props.teamsData}
            onCreateNewTeam={onCreateTeam}
            searchText={searchText}
            setInputs={onSelectTeam}
            setSearchText={setSearchText} // Added setSearchText prop
          />
          <Button
            color="primary"
            style={{ marginLeft: '5px' }}
            onClick={() => {
              if (!searchText) onValidation(false);
              else IfTheUserNotSelectedSuggestionAutoComplete();
            }}
            disabled={isLoading}
          >
            {isLoading ? <Spinner color="light" size="sm" /> : 'Confirm'}
          </Button>
        </div>
        {!isNotDisplayAlert && (
          <>
            <Alert color="danger">Oops, this team does not exist! Create it if you want it.</Alert>
            <div
              style={{
                marginBottom: '10px',
                display: 'flex',
                flexDirection: 'row',
                gap: '10px',
                justifyContent: 'center',
              }}
            >
              {/* prettier-ignore  */}
              <Button color="info" onClick={onCreateTeam}><b>Create Team</b></Button>
              {/* prettier-ignore  */}
              <Button color="danger" onClick={() => setIsNotDisplayAlert(true)}><b>Cancel team creation </b></Button>
            </div>
          </>
        )}

        {!isValidTeam && !searchText && (
          <Alert color="danger">Hey, You need to pick a team first!</Alert>
        )}

        {!isValidNewTeam && !isDuplicateTeam ? (
          <Alert color="danger">Please enter a team name.</Alert>
        ) : null}
        {isDuplicateTeam && <Alert color="danger">A team with this name already exists</Alert>}
      </ModalBody>
      <ModalFooter className={darkMode ? 'bg-yinmn-blue' : ''}>
        <Button color="secondary" onClick={closePopup}>
          Close
        </Button>
      </ModalFooter>
    </Modal>
  );
});

export default AddTeamPopup;

