import React, { useState } from 'react';
import { Modal, ModalHeader, ModalBody, Button } from 'reactstrap';
import './UserProfileModal.css'; // For custom styling
import axios from 'axios';
import { ENDPOINTS } from 'utils/URL';
import { toast } from 'react-toastify';

const ProfileImageModal = ({ isOpen, toggleModal, userProfile }) => {
  const [selectedImage, setSelectedImage] = useState(null);
  const suggestedProfilePics = userProfile.suggestedProfilePics;
  const handleImageSelect = image => {
    setSelectedImage(image); // Store the selected image info
  };

  function getImageSource(image) {
    if (image.nitro_src !== null && image.nitro_src !== undefined) {
      return image.nitro_src;
    } else if (image.src && image.src.startsWith('http')) {
      return image.src;
    } else if (image.data_src !== undefined) {
      return image.data_src;
    }
    return null; // Return null if no valid source is found
  }

  const updateProfileImage = async () => {
    try {
      let image = getImageSource(selectedImage);
      await axios.put(ENDPOINTS.USERS_UPDATE_PROFILE_FROM_WEBSITE, {
        selectedImage: image,
        user_id: userProfile._id,
      });
      toast.success('Profile Image Updated');
    } catch (error) {
      console.log(error);
      toast.error('Image Update Failed');
    }
  };

  return (
    <Modal isOpen={isOpen} toggle={toggleModal}>
      <ModalHeader toggle={toggleModal}>Select a Profile Image</ModalHeader>
      <ModalBody>
        <div className="suggestedProfileLinks scrollable-container">
          {suggestedProfilePics.map((image, index) => (
            <div
              key={index}
              className={`suggestedProfileTile ${selectedImage === image ? 'selected' : ''}`}
              onClick={() => handleImageSelect(image)}
            >
              <img src={getImageSource(image)} alt={image.alt} />
            </div>
          ))}
        </div>

        <div className="button-group">
          <Button color="secondary" onClick={toggleModal} className="modal-button">
            Close
          </Button>
          <Button
            color="primary"
            onClick={() => {
              if (selectedImage) {
                toggleModal(); // Close the modal after setting the image
                updateProfileImage();
              }
            }}
            className="modal-button"
          >
            Set Image
          </Button>
        </div>
      </ModalBody>
    </Modal>
  );
};

export default ProfileImageModal;

import React, { useState, useEffect } from 'react';
import { Row, Label, Input, Col, Button, FormGroup, FormFeedback } from 'reactstrap';
import moment from 'moment-timezone';
import { capitalize } from 'lodash';
import { ENDPOINTS } from '~/utils/URL';
import axios from 'axios';
import HistoryModal from './HistoryModal';
import './timeTab.css';
import { boxStyle, boxStyleDark } from '~/styles';
import {
  formatDate,
  formatDateYYYYMMDD,
  formatDateMMDDYYYY,
  CREATED_DATE_CRITERIA,
} from '~/utils/formatDate';

const MINIMUM_WEEK_HOURS = 0;
const MAXIMUM_WEEK_HOURS = 168;
const darkModeClass = 'bg-darkmode-liblack text-light border-0';

const startEndDateValidation = props => {
  return (
    props.userProfile.startDate > props.userProfile.endDate && props.userProfile.endDate !== ''
  );
};

const StartDate = props => {
  const { darkMode, startDateAlert } = props;
  //determine which date to display
  const displayDate = props.userProfile.isStartDateManuallyModified
    ? props.userProfile.startDate
    : props.calculatedStartDate || props.userProfile.startDate;

  if (!props.canEdit) {
    return (
      <p className={darkMode ? 'text-azure' : ''}>
        {!displayDate ? 'N/A' : formatDate(displayDate)}
      </p>
    );
  }
  return (
    <FormGroup>
      <Input
        type="date"
        name="StartDate"
        id="startDate"
        className={`${startEndDateValidation(props) ? 'border-error-validation' : ''} ${
          darkMode ? 'bg-darkmode-liblack text-light border-0 calendar-icon-dark' : ''
        }`}
        value={displayDate}
        min={
          props.userProfile.createdDate < CREATED_DATE_CRITERIA ? '' : props.userProfile.createdDate
        }
        onChange={e => {
          props.setUserProfile({ ...props.userProfile, startDate: e.target.value });
          props.onStartDateComponent(e.target.value);
        }}
        placeholder="Start Date"
        invalid={!props.canEdit}
        max={props.userProfile.endDate || '9999-12-31'}
      />
      {startDateAlert && <FormFeedback style={{ display: 'block' }}>{startDateAlert}</FormFeedback>}
    </FormGroup>
  );
};

const EndDate = props => {
  const { darkMode, endDateAlert } = props;

  if (!props.canEdit) {
    return (
      <p className={darkMode ? 'text-azure' : ''}>
        {props.userProfile.endDate ? formatDate(props.userProfile.endDate) : 'N/A'}
      </p>
    );
  }

  return (
    <FormGroup>
      <Input
        className={`${startEndDateValidation(props) ? 'border-error-validation' : ''} ${
          darkMode ? 'bg-darkmode-liblack text-light border-0 calendar-icon-dark' : ''
        }`}
        type="date"
        name="EndDate"
        id="endDate"
        value={props.userProfile.endDate ? props.userProfile.endDate : ''}
        onChange={e => {
          props.setUserProfile({ ...props.userProfile, endDate: e.target.value });
          props.onEndDateComponent(e.target.value);
        }}
        placeholder="End Date"
        invalid={!props.canEdit}
        min={props.userProfile.startDate ? props.userProfile.startDate : ''}
        max={'9999-12-31'}
      />
      {endDateAlert && <FormFeedback style={{ display: 'block' }}>{endDateAlert}</FormFeedback>}
    </FormGroup>
  );
};

const WeeklySummaryOptions = props => {
  const { darkMode } = props;

  if (!props.canEdit) {
    return (
      <p className={darkMode ? 'text-azure' : ''}>
        {props.userProfile.weeklySummaryOption ??
          (props.userProfile.weeklySummaryNotReq ? 'Not Required' : 'Required')}
      </p>
    );
  }

  const summaryOptions = [
    { value: 'Required', text: 'Required' },
    { value: 'Not Required', text: 'Not Required (Slate Gray)' },
    { value: 'Team Fabulous', text: 'Team Fabulous (Fuschia)' },
    { value: 'Team Marigold', text: 'Team Marigold (Orange)' },
    { value: 'Team Luminous', text: 'Team Luminous (Yellow)' },
    { value: 'Team Lush', text: 'Team Lush (Green)' },
    { value: 'Team Skye', text: 'Team Skye (Blue)' },
    { value: 'Team Azure', text: 'Team Azure (Indigo)' },
    { value: 'Team Amethyst', text: 'Team Amethyst (Purple)' },
  ];

  const handleOnChange = e => {
    let temp = { ...props.userProfile };
    temp.weeklySummaryOption = e.target.value;
    if (e.target.value === 'Not Required') {
      temp.weeklySummaryNotReq = true;
    } else {
      temp.weeklySummaryNotReq = false;
    }
    props.setUserProfile(temp);
  };

  return (
    <FormGroup>
      <select
        name="WeeklySummaryOptions"
        id="weeklySummaryOptions"
        className={`form-control ${darkMode ? darkModeClass : ''}`}
        disabled={!props.canEdit}
        value={
          props.userProfile.weeklySummaryOption ??
          (props.userProfile.weeklySummaryNotReq ? 'Not Required' : 'Required')
        }
        onChange={handleOnChange}
      >
        {summaryOptions.map(({ value, text }) => (
          <option key={value} value={value}>
            {text}
          </option>
        ))}
      </select>
    </FormGroup>
  );
};

const WeeklyCommittedHours = props => {
  //Do Not change the property name "weeklycommittedHours"
  //Otherwise it will not update in the backend.

  const { darkMode } = props;

  if (!props.canEdit) {
    return <p className={darkMode ? 'text-azure' : ''}>{props.userProfile.weeklycommittedHours}</p>;
  }
  const handleChange = e => {
    // Max: 168 hrs  Min: 0 hr
    // Convert value from string into easy number
    const value = parseInt(e.target.value);
    if (value > MAXIMUM_WEEK_HOURS) {
      // Check if Value is greater than maximum hours and set it to maximum hours if needed
      alert(`You can't commit more than ${MAXIMUM_WEEK_HOURS} hours per week.`);
      props.setUserProfile({ ...props.userProfile, weeklycommittedHours: MAXIMUM_WEEK_HOURS });
    } else if (value < MINIMUM_WEEK_HOURS) {
      //Check if value is less than minimum hours and set it to minimum hours if needed
      alert(`You can't commit less than ${MINIMUM_WEEK_HOURS} hours per week.`);
      props.setUserProfile({ ...props.userProfile, weeklycommittedHours: MINIMUM_WEEK_HOURS });
    } else {
      //update weekly hours whatever numbers in the input
      props.setUserProfile({ ...props.userProfile, weeklycommittedHours: value });
    }
  };

  return (
    <Input
      type="number"
      min={MINIMUM_WEEK_HOURS - 1}
      max={MAXIMUM_WEEK_HOURS + 1}
      name="weeklycommittedHours"
      id="weeklycommittedHours"
      data-testid="weeklycommittedHours"
      value={props.userProfile.weeklycommittedHours}
      onChange={e => handleChange(e)}
      placeholder="Weekly Committed Hours"
      className={darkMode ? darkModeClass : ''}
    />
  );
};

const MissedHours = props => {
  const { darkMode } = props;

  if (!props.canEdit) {
    return <p className={darkMode ? 'text-azure' : ''}>{props.userProfile.missedHours ?? 0}</p>;
  }
  return (
    <Input
      type="number"
      name="missedHours"
      id="missedHours"
      data-testid="missedHours"
      value={props.userProfile.missedHours ?? 0}
      className={darkMode ? darkModeClass : ''}
      onChange={e => {
        props.setUserProfile({
          ...props.userProfile,
          missedHours: Math.max(Number(e.target.value), 0),
